import { useState } from "react";
import "./styles.css";

export default function App() {
  const [meaning, setMeaning] = useState("");

  const emojiDictionary = {
    "ğŸ•": "Dog",
    "ğŸ¦®": "Guide Dog",
    "ğŸˆ": "Cat",
    "ğŸ¦Š": "Fox",
    "ğŸ¦": "Raccoon",
    "ğŸº": "Wolf",
    "ğŸ¦“": "Zebra",
    "ğŸ¦Œ": "Deer",
    "ğŸ‚": "Ox"
  };

  var emojiWeKnow = Object.keys(emojiDictionary);

  function inputEventListener(event) {
    // setUserInput(event.target.value);
    var userInput = event.target.value;

    var meaning = emojiDictionary[userInput];
    if (meaning === undefined) {
      meaning = "Emoji not found. Please enter from defined database.";
    }
    setMeaning(meaning);
  }

  function emojiClickHandler(emoji) {
    var meaning = emojiDictionary[emoji];
    setMeaning(meaning);
  }

  return (
    <div className="App">
      <h1>Emoji Dictionary</h1>
      <input onChange={inputEventListener}></input>
      <div className="output">{meaning}</div>
      <h3> Emojis </h3>
      <div className="container container-center">
        {emojiWeKnow.map(function (emoji) {
          return (
            <span
              onClick={() => emojiClickHandler(emoji)}
              className="spanEmoji"
              key={emoji}
            >
              {emoji}{" "}
            </span>
          );
        })}
      </div>

      <footer className="footer">
        <div className="footer-head">Connect with me on</div>
        <ul className="social-links list-non-bullet">
          <li className="list-item-inline">
            <img
              className="footer-img"
              src="data:image/svg+xml;base64,"
            />
            <a class="link" href="https://github.com/GenxTheGamer">
              Github
            </a>
          </li>
          <li className="list-item-inline">
            <img
              className="footer-img"
              src="data:image/svg+xml;base64,"
            />
            <a class="link" href="https://www.linkedin.com/in/vaibhavvyas32/">
              Linkedin
            </a>
          </li>
          <li className="list-item-inline">
            <img
              className="footer-img"
              src="data:image/svg+xml;base64,"
            />
            <a class="link" href="https://twitter.com/genxthegamer">
              Twitter
            </a>
          </li>
        </ul>
      </footer>
    </div>
  );
}
